'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var ln = require( '@stdlib/math/base/special/ln' );
var EPS = require( '@stdlib/constants/float64/eps' );
var tryRequire = require( '@stdlib/utils/try-require' );


// VARIABLES //

var xlogy = tryRequire( resolve( __dirname, './../lib/native.js' ) );
var opts = {
	'skip': ( xlogy instanceof Error )
};


// TESTS //

tape( 'main export is a function', opts, function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof xlogy, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function has two parameters', opts, function test( t ) {
	t.equal( xlogy.length, 2.0, 'arity is 2' );
	t.end();
});

tape( 'the function returns `NaN` if provided `NaN` as either of the arguments', opts, function test( t ) {
	t.equal( isnan( xlogy( 2.0, NaN ) ), true, 'returns NaN' );
	t.equal( isnan( xlogy( NaN, 3.0 ) ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `NaN` if provided a negative number for `y`', opts, function test( t ) {
	t.equal( isnan( xlogy( 2.0, -0.4 ) ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `NaN` if provided `0` for `y`', opts, function test( t ) {
	t.equal( isnan( xlogy( 2.0, 0.0 ) ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `0` if provided `0` for `x` and not `NaN` for `y`', opts, function test( t ) {
	t.equal( xlogy( 0.0, 2.0 ), 0.0, 'returns 0' );
	t.end();
});

tape( 'the function returns `x * ln(y)`', opts, function test( t ) {
	var y;
	var x;
	var i;

	for ( i = 0; i < 100; i++ ) {
		x = round( randu() * 100.0 ) + EPS;
		y = round( randu() * 5.0 ) + EPS;
		t.equal( xlogy( x, y ), x * ln( y ), 'returns x * ln(y)' );
	}
	t.end();
});
